
CREATE TABLE install_orders (
    af_install_time_d TEXT,
    af_country_code TEXT,
    uid TEXT,
    af_device_id TEXT,
    primaryorderid_fill TEXT,
    ordertype TEXT,
    orderdate TEXT
);



CREATE TABLE signups (
    uid TEXT,
    signup_date TEXT
);



CREATE TABLE visits (
    visit_date TEXT,
    uid TEXT,
    deviceid TEXT,
    page_product_type TEXT,
    uv TEXT,
    sessions TEXT
);


WITH app_installs AS (
    SELECT DISTINCT uid, MIN(af_install_time_d) AS install_date
    FROM install_orders
    WHERE af_device_id IS NOT NULL
    GROUP BY uid
),

signup_after_install AS (
    SELECT ai.uid, ai.install_date, us.signup_date
    FROM app_installs ai
    JOIN signups us ON ai.uid = us.uid
    WHERE DATE(us.signup_date) >= DATE(ai.install_date)
),

flight_orders_within_7days AS (
    SELECT DISTINCT sai.uid
    FROM signup_after_install sai
    JOIN install_orders io ON sai.uid = io.uid
    WHERE io.primaryorderid_fill IS NOT NULL
      AND io.ordertype = 'F'                      -- Only flight orders
      AND DATEDIFF(io.orderdate, sai.signup_date) BETWEEN 0 AND 7  -- Within 7 days of signup
)

SELECT 
    (SELECT COUNT(DISTINCT uid) FROM signup_after_install) AS total_new_users,
    (SELECT COUNT(DISTINCT uid) FROM flight_orders_within_7days) AS converting_users,
    ROUND(
        (SELECT COUNT(DISTINCT uid) FROM flight_orders_within_7days) * 100.0 / 
        (SELECT COUNT(DISTINCT uid) FROM signup_after_install),
        2
    ) AS conversion_rate;
